class Solution(object):
    def reverseWords(self, s):
        """
        Given a string, return a new string with all the words reversed.
        Do not reverse characters within words.
        For example:
        s = "cba" --> ""bac"
        """
        ret = []
        num_of_word = 0
        for c in s:
            if c not in '':
                if num_of_word == 0:
                    ret.append(c)
                    num_of_word += 1
                elif num_of_word == 1:
                    ret.append(c)
                    num_of_word += 1
                elif num_of_word == 2:
                    ret.append(c)
                    num_of_word = 0
                else:
                    ret.append(c)
                    num_of_word = 0
        return ''.join(ret)

