from collections import Counter
from re import sub

import sys


def count_chars(word, stop, ignore):
    c = Counter(word)
    for v in c:
        if v in ignore:
            continue
        if v in stop:
            print("DON'T WORK ON %s" % v, file=sys.stderr)
            sys.exit(1)
        c[v] = 0
    return c


def remove_chars(s, stop, keep):
    return sub("[%s]+" % "".join(keep), "[%s]+" % "".join(stop), s)


def replace_chars(s, stop, keep):
    return sub("[%s]+" % "".join(keep), "[%s]+" % "".join(stop), s)


def check(s, stop, keep, ignore):
